var Discord = require('discord.js');

class discordEmbed {
    async simpleEmbed (title, description, color) {
        try {
            var embed2 = new Discord.RichEmbed();
            embed2.setTitle(title);
            embed2.setDescription(description);
            embed2.setColor(color);
        } catch (ex) {
            throw ex;
        }
    }
    async richEmbed (options) {
        if (options.title) var title = options.title;
        if (options.color) var color = options.color;
        if (options.footer) var footer = options.footer;
        if (options.image) var image = options.image;
        if (options.author) var author = options.author;
        if (options.timeStamp) var timeStamp = options.timeStamp;
        if (options.thumbnail) var thumbnail = options.thumbnail;
        if (options.url) var embedURL = options.url;

        try {
        var embed = new Discord.RichEmbed();

        if (title) embed.setTitle(title);
        if (color) embed.setColor(color);
        if (footer) embed.setFooter(footer);
        if (image) embed.setImage(image);
        if (author) embed.setAuthor(author);
        if (timeStamp) embed.setTimestamp(timeStamp);
        if (thumbnail) embed.setThumbnail(thumbnail);
        if (embedURL) embed.setURL(embedURL);
        return embed;
        } catch (ex) {
            throw ex;
        }
    }
    async classicPing(ping) {
        try {
            var str = `The Bot Has ${Math.floor(ping)} Ping!`;
            return str;
        } catch (e) {
            throw e;
        }
    }
    async editEmbed(client, message, firstEmbed, lastEmbed, miliseconds) {
        let time = Number.parseInt(miliseconds);
        const msg = await message.channel.send({firstEmbed});
        client.setTimeout(function () {
            msg.edit({lastEmbed})
        }, time)
        return;
    }
}


module.exports = new discordEmbed();